    return player.UserId
    end

    --------------------------------------------------
    -- UTILS
    --------------------------------------------------
    local function jsonEncode(t)
        return HttpService:JSONEncode(t)
        end

        local function jsonDecode(t)
            return HttpService:JSONDecode(t)
            end

            local function digest(str)
                str = tostring(str)
                    local out = {}
                        for i = 1, #str do
                                out[#out+1] = string.format("%02x", string.byte(str, i))
                                    end
                                        return table.concat(out)
                                        end

                                        local function genNonce()
                                            local s = ""
                                                for i = 1, 16 do
                                                        s ..= string.char(math.random(97, 122))
                                                            end
                                                                return s
                                                                end

                                                                --------------------------------------------------
                                                                -- FILE SYSTEM
                                                                --------------------------------------------------
                                                                local function saveKey(key)
                                                                    if writefile then
                                                                            writefile(KEY_FILE, key)
                                                                                end
                                                                                end

                                                                                local function loadKey()
                                                                                    if isfile and isfile(KEY_FILE) then
                                                                                            return readfile(KEY_FILE)
                                                                                                end
                                                                                                end

                                                                                                local function clearKey()
                                                                                                    if delfile and isfile(KEY_FILE) then
                                                                                                            delfile(KEY_FILE)
                                                                                                                end
                                                                                                                end

                                                                                                                --------------------------------------------------
                                                                                                                -- HOST DETECT
                                                                                                                --------------------------------------------------
                                                                                                                local HOST = "https://api.platoboost.app"
                                                                                                                local test = request({
                                                                                                                    Url = HOST .. "/public/connectivity",
                                                                                                                        Method = "GET"
                                                                                                                        })
                                                                                                                        if not test or test.StatusCode ~= 200 then
                                                                                                                            HOST = "https://api.platoboost.net"
                                                                                                                            end

                                                                                                                            --------------------------------------------------
                                                                                                                            -- PLATOBOOST VERIFY
                                                                                                                            --------------------------------------------------
                                                                                                                            local function redeemKey(key)
                                                                                                                                local nonce = genNonce()
                                                                                                                                    local res = request({
                                                                                                                                            Url = HOST .. "/public/redeem/" .. SERVICE_ID,
                                                                                                                                                    Method = "POST",
                                                                                                                                                            Headers = {["Content-Type"] = "application/json"},
                                                                                                                                                                    Body = jsonEncode({
                                                                                                                                                                                identifier = digest(gethwid()),
                                                                                                                                                                                            key = key,
                                                                                                                                                                                                        nonce = nonce
                                                                                                                                                                                                                })
                                                                                                                                                                                                                    })

                                                                                                                                                                                                                        if res and res.StatusCode == 200 then
                                                                                                                                                                                                                                local data = jsonDecode(res.Body)
                                                                                                                                                                                                                                        return data.success == true
                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                return false
                                                                                                                                                                                                                                                end

                                                                                                                                                                                                                                                local function verifyKey(key)
                                                                                                                                                                                                                                                    if not key or not key:match("^" .. REQUIRED_PREFIX) then
                                                                                                                                                                                                                                                            return false
                                                                                                                                                                                                                                                                end

                                                                                                                                                                                                                                                                    local nonce = genNonce()
                                                                                                                                                                                                                                                                        local url =
                                                                                                                                                                                                                                                                                HOST .. "/public/whitelist/" .. SERVICE_ID ..
                                                                                                                                                                                                                                                                                        "?identifier=" .. digest(gethwid()) ..
                                                                                                                                                                                                                                                                                                "&key=" .. key ..
                                                                                                                                                                                                                                                                                                        "&nonce=" .. nonce

                                                                                                                                                                                                                                                                                                            local res = request({ Url = url, Method = "GET" })
                                                                                                                                                                                                                                                                                                                if not res or res.StatusCode ~= 200 then
                                                                                                                                                                                                                                                                                                                        return false
                                                                                                                                                                                                                                                                                                                            end

                                                                                                                                                                                                                                                                                                                                local data = jsonDecode(res.Body)
                                                                                                                                                                                                                                                                                                                                    if data.success and data.data.valid then
                                                                                                                                                                                                                                                                                                                                            return true
                                                                                                                                                                                                                                                                                                                                                end

                                                                                                                                                                                                                                                                                                                                                    -- FREE KEY ‚Üí Redeem
                                                                                                                                                                                                                                                                                                                                                        if key:sub(1,5) == "FREE_" then
                                                                                                                                                                                                                                                                                                                                                                return redeemKey(key)
                                                                                                                                                                                                                                                                                                                                                                    end

                                                                                                                                                                                                                                                                                                                                                                        return false
                                                                                                                                                                                                                                                                                                                                                                        end

                                                                                                                                                                                                                                                                                                                                                                        --------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                        -- AUTO LOAD SAVED KEY
                                                                                                                                                                                                                                                                                                                                                                        --------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                        local saved = loadKey()
                                                                                                                                                                                                                                                                                                                                                                        if saved and verifyKey(saved) then
                                                                                                                                                                                                                                                                                                                                                                            loadstring(game:HttpGet(SCRIPT_TO_LOAD))()
